import { IUCNCategory } from '@marxan/iucn';

export const insertWdpaQuery = (iucnCategory: IUCNCategory) => {
  return `
INSERT INTO wdpa (wdpaid, the_geom, full_name, iucn_cat, shape_leng, shape_area, iso3, status, desig, created_by)
VALUES
  (17999, ''::geometry,
   null, '${iucnCategory}', null, null, 'NAM', 'Designated', 'National Park', '00000000-0000-0000-0000-000000000000')`;
};
